import re
from bs4 import BeautifulSoup
import mysql.connector
import urllib3
from com.eason.web.crawler.BaseCrawler import BaseCrawler

class JdCrawler(BaseCrawler):
    def __init__(self):
        self.name = 'BaseClass'
        print('BaseCalss: Constructor called')

    def crawlCat(self):
        http = urllib3.PoolManager()
        r = http.request('GET', 'http://www.jd.com/allSort.aspx')
        #print(r.data.decode("gbk"))

        data = r.data.decode("gbk")

        soup = BeautifulSoup(data) # html为html源代码字符串，type(html) == str
        content = soup.find("div", {"id": "allsort"})

        contents = content.findAll("a", {"href": re.compile("^http://list.jd.com/")})

        data = []
        for cat in contents:
            data.append((cat.get('href'),cat.string.__str__()))
        return data

    def persistent(self,data):
        config = {'host': 'localhost', #默认127.0.0.1
                  'user': 'root',
                  'password': 'root',
                  'port': 3306, #默认即为3306
                  'database': 'test',
                  'charset': 'utf8'#默认即为utf8
        }

        cnn = mysql.connector.connect(**config)
        cursor = cnn.cursor()
        stmt = "insert into jdcrawler (cat, name) values (%s, %s)"

        try:
            cursor.executemany(stmt,data)
            cnn.commit()
        finally:
            cursor.close()
            cnn.close()


if __name__ == '__main__':
    jdCrawler = JdCrawler()
    jdCrawler.test()
    data = jdCrawler.crawlCat()
    print(data)
